class Solution {
public:
    double myPow(double x, int n) {
        if(n == 0)return 1;
        double res = 1;

        /*用N代替N是因为若n = -2^31次方时，直接转换为正数会超出int型的表示范围*/
        long long int N = n;
        if(N  0)
        {
            if(N%2 != 0)
                res *= x;
            N = N/2;
            x *= x;
        }

        return res*x;
    }
};

