/*最基础的链表题，但写起来并不熟练*/

/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode(int x) : val(x), next(NULL) {}
 * };
 */
class Solution {
public:
    ListNode* deleteNode(ListNode* head, int val) {
        ListNode *pre,*aft;
        if(head == nullptr)return nullptr;

        pre = head;
        aft = pre->next;
        if(pre->val == val)
            return pre->next;

        while(aft != nullptr && aft->val != val)
        {
            pre = aft;
            aft = aft->next;
        }
        if(aft != nullptr)
               pre->next = aft->next;
        return head;
    }
};

