class Solution {
public:
    ListNode* getKthFromEnd(ListNode* head, int k) {
        ListNode *pre,*las;
        pre = las = head;
        while(las->next != nullptr && k-- > 0)
        {
            las = las->next;
        }
        if(las->next == nullptr && k != 0)
            return pre;
        while(las != nullptr)
        {
            pre = pre->next;
            las = las->next;
        }

        return pre;
    }
};

