/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode(int x) : val(x), next(NULL) {}
 * };
 */
class Solution {
private:
    ListNode *newHead;
public:
    ListNode* func(ListNode* head)
    {
         if(head->next == nullptr)
        {
            newHead = head;
            return head;
        }
        func(head->next)->next = head;
        return head;
    }
    ListNode* reverseList(ListNode* head) {
        if(head == nullptr)
            return nullptr;
        func(head);
        head->next = nullptr;
        return newHead;
    }
};

合并两个排序的链表
/**插入排序**/
/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode(int x) : val(x), next(NULL) {}
 * };
 */
class Solution {
public:
    ListNode* mergeTwoLists(ListNode* l1, ListNode* l2) {
        ListNode *head;

        if(l1 == nullptr)
            return l2;
        if(l2 == nullptr)
            return l1;

        ListNode *ptr,*cur;

        if(l1->val > l2->val)
        {
            head = l2;
            l2 = l2->next;
        }
        else
        {
            head = l1;
            l1 = l1->next;
        }

        ptr = head;

        while(l1 != nullptr && l2 != nullptr)
        {
            if(l1->val > l2->val)
            {
                cur = l2;
                l2 = l2->next;
            }
            else
            {
                cur = l1;
                l1 = l1->next;
            }

            ptr->next = cur;
            ptr = ptr->next;
        }

        while(l1 != nullptr)
        {
            cur = l1;
            l1 = l1->next;
            ptr->next = cur;
            ptr = ptr->next;
        }

        while(l2 != nullptr)
        {
            cur = l2;
            l2 = l2->next;
            ptr->next = cur;
            ptr = ptr->next;
        }

        return head;
    }
};

