#define MIN(X,Y) ((X > Y)?(Y):(X))
class MinStack {
public:
    class Node
    {
        public:
        int val;
        int min;
        Node *next;

        Node(int val_, int min_, Node *next_)
        {
            val = val_;
            min = min_;
            next = next_;
        }
    };

    Node *head;
public:
    /** initialize your data structure here. */
    MinStack() {
        head = nullptr;
    }

    void push(int x) {
        if(head == nullptr)
            head = new Node(x,x,nullptr);
        else
            head = new Node(x,MIN(x,head->min),head);
    }

    void pop() {
        Node *cur = head->next;
        delete head;
        head = cur;
    }

    int top() {
        return head->val;
    }

    int min() {
        return head->min;
    }
};

