class Solution {
private:
    Node *head,*tail;
    void midorder(Node *root)
    {
        if(root != nullptr)
        {
            midorder(root->left);
            if(head == nullptr)
                tail = head = root;
            else
            {
                root->left = tail;
                tail->right = root;
                tail = root;
            }

            midorder(root->right);
        }
    }
public:
    Node* treeToDoublyList(Node* root) {
        if(root == nullptr)
            return nullptr;

        head = tail = nullptr;
        midorder(root);

        head->left = tail;
        tail->right = head;

        return head;
    }
};

