//两个链表中相同节点前的节点数
/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode(int x) : val(x), next(NULL) {}
 * };
 */
class Solution {
public:
    ListNode *getIntersectionNode(ListNode *headA, ListNode *headB) {
        if(headA == nullptr || headB == nullptr)
            return nullptr;

        ListNode *curA = headA;
        ListNode *curB = headB;

        int count = 0;
        while(curA != curB && count next != nullptr?(curA->next):(count++,headB);
            curB = curB->next != nullptr?curB->next:headA;
        }

        if(count == 2)
            return nullptr;
        return curA;
    }
};

