class Solution {
public:
    string reverseWords(string s) {
        int len = s.length();
        int left, right;
        string res;

        for(int i = len - 1; i >= 0; i--)
        {
            if(s[i] == ' ')
                continue;
            else
            {
                right = i;
                while(i >= 0 && s[i] != ' '){i--;}
                left = i + 1;

                res += s.substr(left, right - left + 1);
                res += ' ';
            }
        }

        return res.substr(0, res.length() - 1);
    }
};

